import React, { Component } from 'react'
import App, { EventTags } from './App'


function shuffle(array){
  let counter = array.length

  while (counter > 0) {
    let index = Math.floor(Math.random() * counter)

    counter--

    let temp = array[counter]
    array[counter] = array[index]
    array[index] = temp
  }

  return array
}


class Header extends Component {


	render() {
	return (
		<React.Fragment>
			<h1><span className="blue">&lt;</span>Task<span className="blue">&gt;</span> <span className="yellow">Manager</span></h1>
		<h2>Created with love by <a href="https://github.com/aggads?tab=repositories"> Samy</a></h2>
		<div id="container">   	
			<p><a>Vivyane</a></p>
			<p><a>Samy</a></p>
			<p><a>Pierre</a></p>
			<p><a>Flore</a></p>
			<p><a>Delaine</a></p>
			<p><a>		
				<button className="button" onClick={this.handleClick}>
			      Shuffle !
			      </button>
			</a></p>
		</div>
		</React.Fragment>

			)
	}
}

export default Header
