import React, { Component } from 'react'
import './App.css'
import Header from './Header'
import Table from './Table'

const roomate = ['Samy', 'Flore', 'Vivyane', 'Pierre', 'Delaine']

function shuffleArray(array) {
  let i = array.length - 1;
  for (; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    const temp = array[i];
    array[i] = array[j];
    array[j] = temp;
  }
  return array;
}

class App extends Component {

  state = {
    roomate
  }

handleClick = () => {
  //Duplicate the array
  const roomate = { ... this.state.roomate }

  //Shuffle the array

  const shuffledroomate = shuffleArray(roomate)

  //Update the array
  this.setState({ roomate })
  }





  render() {

    const shuffledroomate = shuffleArray(roomate)

    return (
      <div id="App">
      <Header />
      <table className="container">
      <thead>
        <tr>
          <th><h1>Week of</h1></th>
          <th><h1>14/01 </h1></th>
          <th><h1>21/01</h1></th>
          <th><h1>28/01</h1></th>
        </tr>
      </thead>
      <tbody className="blabla">
        <tr>
          <td  className="week1">Salon</td>
          <td  className="case1">{shuffledroomate.map}</td>
          <td  className="case2"></td>
          <td  className="case3"></td>
          <td  className="case4"></td>
          <td  className="case5"></td>
        </tr>
        <tr>
          <td  className="week2">Cuisine</td>
          <td  className="case1"></td>
          <td  className="case2"></td>
          <td  className="case3"></td>
          <td  className="case4"></td>
          <td  className="case5"></td>
        </tr>
        <tr>
          <td className="week3">Palier</td>
          <td  className="case1"></td>
          <td  className="case2"></td>
          <td  className="case3"></td>
          <td  className="case4"></td>
          <td  className="case5"></td>
        </tr>
        <tr>
          <td className="week4">Toilette</td>
          <td  className="case1"></td>
          <td  className="case2"></td>
          <td  className="case3"></td>
          <td  className="case4"></td>
          <td  className="case5"></td>
        </tr>
        <tr>
          <td className="week5">Salle de bain</td>
          <td  className="case1"></td>
          <td  className="case2"></td>
          <td  className="case3"></td>
          <td  className="case4"></td>
          <td  className="case5"></td>
        </tr>
      </tbody>
    </table>

      </div>


    );
  }
}

export default App;
