import React, { Component } from 'react'
import './App.css'
import Header from './Header'
// import Table from './Table'

const roomates = ['Samy', 'Flore', 'Vivyane', 'Pierre', 'Delaine']
// {
//   roomate:{
//     name: 'Samy'
//   },
