//     name: 'Vivyane'
//   },
//   member4:{
//     name: 'Pierre'
//   },
//   member5:{
//     name: 'Delaine'
//   },
// }

function shuffle(array){
  let counter = array.length

  while (counter > 0) {
    let index = Math.floor(Math.random() * counter)

    counter--

    let temp = array[counter]
    array[counter] = array[index]
    array[index] = temp
  }

  return array
}




class App extends Component {

  state = {
    roomates: ''
  }

handleClick = (roomate) => {
  //Duplicate the array
  const roomates = { ...this.state.roomates }

  //Shuffle the array
  roomate = shuffle(roomates)


  //Update the array
  this.setState({ roomates })

  console.log(roomates)
  console.log(roomate)
  }




  render() {
    const { roomates } = this.state
    return (
      <div id="App">
      <Header />
      <table className="container">
      <thead>
        <tr>
          <th><h1>Week of</h1></th>
          <th><h1>14/01 </h1></th>
          <th><h1>21/01</h1></th>
          <th><h1>28/01</h1></th>
        </tr>
      </thead>
      <tbody className="blabla">
        <tr>
          <td  className="week1">Salon</td>
          <td  className="case1">{roomates[1]}</td>
          <td  className="case2">{roomates[2]}</td>
          <td  className="case3">{roomates[0]}</td>
          <td  className="case4"></td>
          <td  className="case5"></td>
        </tr>
        <tr>
          <td  className="week2">Cuisine</td>
          <td  className="case1"></td>
          <td  className="case2"></td>
          <td  className="case3"></td>
          <td  className="case4"></td>
          <td  className="case5"></td>
        </tr>
        <tr>
          <td className="week3">Palier</td>
          <td  className="case1"></td>
          <td  className="case2"></td>
          <td  className="case3"></td>
          <td  className="case4"></td>
          <td  className="case5"></td>
        </tr>
        <tr>
          <td className="week4">Toilette</td>
          <td  className="case1"></td>
          <td  className="case2"></td>
          <td  className="case3"></td>
          <td  className="case4"></td>
          <td  className="case5"></td>
        </tr>
        <tr>
          <td className="week5">Salle de bain</td>
          <td  className="case1"></td>
          <td  className="case2"></td>
          <td  className="case3"></td>
          <td  className="case4"></td>
          <td  className="case5"></td>
        </tr>
      </tbody>
    </table>

      </div>


    );
  }
}

export default App
