import React, { Component } from 'react';
import './App.css';
import Header from './Header'

const roomate = {
  member1: {
    name: 'Samy'
  },
  member2: {
    name: 'Flore'
  },
  member3: {
    name: 'Vivyane'
  },
  member4: {
    name: 'Pierre'
  },
  member5: {
    name: 'Delaine'
  },
}


class App extends Component {

  state = {
    roomate
  }

handleClick = () => {

  const roomate = { ... this.state.roomate }
  this.setState({ roomate })
  }


  render() {
    return (
      <Header />

    );
  }
}

export default App;
